import React, { Component } from 'react';
import axios from 'axios';


export default class changePassword extends Component {
    constructor(props) {
        super(props);

        this.state = {
            old: '',
            new: '',
            renew:'',
        }
        this.onChangeold = this.onChangeold.bind(this);
        this.onChangenew = this.onChangenew.bind(this);
        this.onChangerenew = this.onChangerenew.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    

    onChangeold(e) {
        this.setState({
            old: e.target.value
        });
    }

    onChangenew(e) {
        this.setState({
            new: e.target.value
        });
    }
    onChangerenew(e) {
        this.setState({
            renew: e.target.value
        });
    }


    onSubmit(e) {
        e.preventDefault();
      
        let id= window.sessionStorage.getItem("id");
        let oldPass = window.sessionStorage.getItem("password");

        console.log(`Form submitted:`);
        console.log(`id: ${id}`);
        console.log(`old: ${this.state.old}`);
        console.log(`new: ${this.state.new}`);
        console.log(`renew: ${this.state.renew}`);
        console.log(`oldPass: ${oldPass}`);
        const user= {
            id:this.id,
            old:this.state.old,
            new:this.state.new,
            renew:this.state.renew,
            oldPass:this.oldPass
        }

        console.log(user)
        
        axios.post('http://localhost:4000/updatepassword', user)
            .then(res => {
                console.log(res.data);
                if(res.data==="success")
                {
                    alert("Password updated Successfuly");
                }
                else
                {
                    alert(res.data);
                }
            });

        this.setState({
            old: '',
            new: '',
            renew:''
        });
    }


    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Change own Password</h3>
                <form onSubmit={this.onSubmit}>
                    
                    
                    <div className="form-group">
                        <label>Password: </label>
                        <input 
                                type="password" 
                                className="form-control"
                                value={this.state.old}
                                onChange={this.onChangeold}
                                />
                    </div>

                    <div className="form-group">
                    <label>Password: </label>
                    <input 
                            type="password" 
                            className="form-control"
                            value={this.state.new}
                            onChange={this.onChangenew}
                            />
                    </div>

                    <div className="form-group">
                    <label>Password: </label>
                    <input 
                            type="password" 
                            className="form-control"
                            value={this.state.renew}
                            onChange={this.onChangerenew}
                            />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary" />
                    </div>
                </form>

            </div>
        )
    }
}
