        const Table = createTableElement();

//        const addMedicineElement = createMedicineElement();


        return (

            <div className="base">

              <div>
              <p  id="newmedicine"  onClick={this.togglePop}></p>
              <br></br>
              </div>

              <div className="t">
              {Table}
              </div>




              <div  style={this.state.seen ? {display:'block'}:{display:'none'}}>
                  <div className="popup">
                  <div className="content">
                  <span className="close"  onClick={this.togglePop}>
                    &times;
                  </span>
                  <form onSubmit={this.onSubmitModal}>
                    <h3>Add Medicine Data!</h3>
                    <br />  
                    <input 
                      type="text" 
                      className="form-control"
                      placeholder="Drug Name"
                      required
                      value={this.state.name}
                      onChange={this.onChangename}
                      id="medicineName"
                      />

                    <br />
                    <input 
                    type="text" 
                    className="form-control"
                    placeholder="Form"
                    required
                    value={this.state.form}
                    onChange={this.onChangeform}
                    id="medicineForm"
                    />

                    <br />
                    <input 
                    type="text" 
                    className="form-control"
                    placeholder="Strength"
                    required
                    value={this.state.strength}
                    onChange={this.onChangestrength}
                    id="medicineStrength"
                    />

                    <br />
                    <input 
                    type="text" 
                    className="form-control"
                    placeholder="Active Ingredient"
                    required
                    value={this.state.ingredient}
                    onChange={this.onChangeingredient}
                    id="medicineIngrdient"
                    />

                    <br />
                    <input type="submit" />
                  </form>
                </div>
            </div>
              </div>

            </div>

        )
    }
}
