import React, { Component } from 'react';
import axios from 'axios';

async function getpatients(con){

    const user= {
        contact:con,
    }

    
    await axios.post('http://localhost:4000/getpatients', user)
        .then(res => {
            if(res.data==="empty")
            {
                console.log(res.data);
                console.log("in axios if");
                
            }
            else
            {
                console.log(res.data);
                console.log("in axios else");
                
            }
        });

        console.log("outside axios");
 

}

function Validatephone(contact) 
{
//    var mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    var phoneno = /^\+?([0-9]{12})\)?$/;
    if(contact.match(phoneno))
    {
    return false;
    }
    else
    {
    return true;
    }
}

function validate(contact) {
    // true means invalid, so our conditions got reversed
    return {
      contact: Validatephone(contact),
    };
}



export default class AddPrescription extends Component {
    constructor(props) {
        super(props);

        this.state = {
            contact: '',
            name:'',
            nameList:[],
            showName:false,
            prescriptions : [],
            touched: {
                contact: false,
              }
            
        }
        this.onChangecontact = this.onChangecontact.bind(this);
        this.onChangename = this.onChangename.bind(this);

    }
    onChangecontact(e) {
        this.setState({
            contact: e.target.value,
            nameList:[]
        });
    }

    onChangename(e) {
        this.setState({
            name: e.target.value
        });
    }

    handleBlur = (field) => (evt) => {
        this.setState({
          touched: { ...this.state.touched, [field]: true },
        });
      } 


    render() {

        const errors = validate(this.state.contact);
        const isDisabled = Object.keys(errors).some(x => errors[x]);

        const shouldMarkError = field => {
            const hasError = errors[field];
            const shouldShow = this.state.touched[field];
      
            return hasError ? shouldShow : false;
          };
    
          const createNameElement = () => {

            if(!Validatephone(this.state.contact))
            {
            
            console.log("before");

            const user= {
                contact:this.state.contact,
            }
            const promise1 = new Promise(function(resolve, reject) {
                
                axios.post('http://localhost:4000/getpatients', user)
                .then(res => {
                    resolve(res.data)
                });
              });
              
              promise1.then((value) =>{
                if(value!="empty")
                this.setState({
                    nameList: value
                });
              });
              

                if(this.state.nameList.length>0)
                {
                    return (
                        <div>
                        <label >Select Patient:</label>
                            <br></br>
                            <select value={this.state.name} onChange={this.onChangename}>
                            <option value="doctor">Doctor</option>
                            <option value="doctor assistant">Doctor Assistant</option>
                            <option value="lab staff">Lab Staff</option>
                            <option value="admin">Admin</option>
                            </select>
                            
                        </div>
                            );            
                }
                else
                {
                    return(
                    <div className="invalid-feedback">
                         No patient exists with this contact.
                    </div>
                    );
                }  

            }
            else
            {
                return (
                    <div>
                    </div>
                    );
            }
          }          
            
        const nameElement = createNameElement(this.state.contact);
              

        return (
            <div>
                <div className="form-group">
                    <label>Contact: </label>
                    <input 
                            type="text" 
                            className={shouldMarkError("contact") ? "form-control is-invalid" : "form-control"}
                            value={this.state.contact}
                            onChange={this.onChangecontact}
                            onBlur={this.handleBlur("contact")}
                            />
                            {shouldMarkError("contact") ?
                                <div className="invalid-feedback">
                                    Please provide a valid contact like +921112223456.
                                </div>
                            :""}

                </div>


                <div>
                {nameElement}
                </div>

            </div>
            
        )
    }
}
