            'Strength': "1972-09-04T11:09:59",
            'Active Ingredient': "green",
        },
        {
            'id': "50cf",
            'Drug Name': "Kerr Mayo",
            'Form': 'true',
            'Strength': "1972-09-04T11:09:59",
            'Active Ingredient': "green",
        }
      ]
    },
    features: {
      canEdit: true,
      canDelete: true,
      canSearch: true,
      canOrderColumns: true,
      canSelectRow: true,

    }
  };


export default class medicineList extends Component {
    
    constructor(props) {
        super(props);

        this.state = {

        }

    }
    
