import React, { Component } from 'react';

import {Datatable} from "@o2xp/react-datatable";
import axios from 'axios';
import AddIcon from '@material-ui/icons/Add';

class PopUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name:''
    }
    this.onChangename = this.onChangename.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onChangename(e) {
    this.setState({
        name: e.target.value
    });
  
  }

  onSubmit(e) {
    e.preventDefault();
  
  
    console.log(`Form submitted:`);
    console.log(`name: ${this.state.name}`);
    const user= {
        name:this.state.name,
    }
    console.log(user)
    
    this.props.func(user)

    this.setState({
        name: '',
    });
  }

  handleClick = () => {
    this.props.toggle();
  };

  render() {
    return (
      <div className="modal">
        <div className="modal_content">
          <span className="close" onClick={this.handleClick}>
            &times;
          </span>

        </div>
      </div>
    );
  }
}


let option = {
    title: "My super datatable",
    dimensions: {
      datatable: {
        width: "90%",
        height: "40%"
      },
      row: {
        height: "48px"
      }
    },
    keyColumn: "id",
    font: "Arial",
    data: {
      columns: [
        {
            id: "id",
            label: "id",
            colSize: "150px",
            editable: false
        },
        {
            id: "Drug Name",
            label: "Name",
            colSize: "100px",
            editable: true,
            dataType: "text",
            inputType: "input"
        },
        {
            id: "Form",
            label: "Form",
            colSize: "100px",
            editable: true,
            dataType: "text",
            inputType: "input"
        },
        {
            id: "Strength",
            label: "Strength",
            colSize: "100px",
            editable: true,
            dataType: "text",
            inputType: "input"
        },
        {
            id: "Active Ingredient",
            label: "Active Ingredient",
            colSize: "100px",
            editable: true,
            dataType: "text",
            inputType: "input"
        },
      
        ],

      rows: []
    },
    features: {
      canEdit: true,
      canDelete: true,
      canSearch: true,
      canOrderColumns: true,
      canSelectRow: true,
      rowsPerPage: {
        available: [10, 25, 50, 100],
        selected: 50
      },
    }
  };

var count = 0

export default class medicineList extends Component {
    
    constructor(props) {
        super(props);

        this.state = {
            gotData:false,
            data:[],
            options:{},
            name:'',
            seen: false,     
        }
        this.onSubmitModal = this.onSubmitModal.bind(this)
        this.togglePop = this.togglePop.bind(this)
        
    }
    
    onSubmitModal(evt) {
      console.log(evt)
      this.setState({
        name:evt.name,
        seen: false
      });
    }
  
    togglePop = () => {
      console.log("toggle pop")
      this.setState({
        seen: !this.state.seen
      });
    };

    actionsRow = ({ type, payload }) => {
        console.log(type);
        console.log(payload);
    };
    

    updateoptions = ()=>
    {

      if(this.state.gotData==false)
      {
        console.log(count++)

        const promise1 = new Promise(function(resolve, reject) {
                      
        axios.post('http://localhost:4000/getMedicines',)
        .then(res => {
            resolve(res.data)
          });
        });
      
        promise1.then((value) =>{
            if(value!="empty")
            this.setState({
                data: value,
                gotData:true,
                options : {
                  title: "Medicine dataTable",
                  dimensions: {
                    datatable: {
                      width: "90%",
                      height: "150%"
                    },
                    row: {
                      height: "48px"
                    }
                  },
                  keyColumn: "id",
                  font: "Arial",
                  data: {
                    columns: [
                      {
                          id: "id",
                          label: "id",
                          colSize: "150px",
                          editable: false
                      },
                      {
                          id: "Drug Name",
                          label: "Name",
                          colSize: "100px",
                          editable: true,
                          dataType: "text",
                          inputType: "input"
                      },
                      {
                          id: "Form",
                          label: "Form",
                          colSize: "100px",
                          editable: true,
                          dataType: "text",
                          inputType: "input"
                      },
                      {
                          id: "Strength",
                          label: "Strength",
                          colSize: "100px",
                          editable: true,
                          dataType: "text",
                          inputType: "input"
                      },
                      {
                          id: "Active Ingredient",
                          label: "Active Ingredient",
                          colSize: "100px",
                          editable: true,
                          dataType: "text",
                          inputType: "input"
                      },
                    
                      ],
          
                    rows: value
                  },
                  features: {
                    canEdit: true,
                    canDelete: true,
                    canSearch: true,
                    canOrderColumns: true,
                    canSelectRow: true,
                    rowsPerPage: {
                      available: [10, 25, 50, 100],
                      selected: 50
                    },
                    additionalIcons: [
                      {
                        title: "Add Medicine",
                        icon: <AddIcon color="primary" />,
                        onClick: () => {this.togglePop()}
                      }
                    ]
                  }
                }
              });
        });

      }

      
    }

    render() {

        const createTableElement = () => {

                this.updateoptions()
                return(
                <div style={{marginTop: 10}}>
                <h3>Medicine List</h3>
                <Datatable
                options={this.state.options}
                actions={this.actionsRow}
                />

                </div>
                )
        }

        const Table = createTableElement();


        return (

            <div>
            {this.state.seen ? <PopUp toggle={this.togglePop} func={this.onSubmitModal} /> : null}
            <div>
              {Table}
            </div>

            </div>

        )
    }
}
