import React, { Component } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table'


export default class DoctorFollowUps extends Component {
    constructor(props) {
        super(props);

        this.state = {
            requestList:[],
            checkRequests:true       
        }
    }


      render() {

         
        const createMedicineList = medicineList =>{
            return medicineList.map((item, index) => {
                const { medicine, dosage,days, morning,afternoon,evening, instructions } = item 
                return (
 
                   <tr>
                      <td >{medicine}</td>
                      <td >dosage: {dosage}</td>
                      <td >days: {days}</td>
                      <td >{morning?"morning":""}</td>
                      <td >{afternoon?"afternoon":""}</td>
                      <td >{evening?"evening":""}</td>
                      <td >{instructions}</td>
                      
                   </tr>
                )
             })
        }

        const createTableElement = () => {

            if(this.state.checkRequests)
            {
                const promise1 = new Promise(function(resolve, reject) {
                    
                    axios.post('http://localhost:4000/getPrescription')
                    .then(res => {
                        resolve(res.data)
                    });
                });
               
                promise1.then((value) =>{
                    console.log(value);
                    if(value!="empty")
                    this.setState({
                        requestList: value,
                        checkPrescription:false,
                    });
                });
            
            }

            if(this.state.requestList.length>0)
            {
                return this.state.prescription.map((item, index) => {
                    const { contact, name,doctor, date, prescription } = item 
                    return (
     
                        <div>
                        <Table striped bordered hover >
                        <thead>
                            <tr>
                                <th>prescription #{index}</th>
                            </tr>
                        </thead>
                        <tbody>
                        <tr>
                            <td >contact:{contact}</td>
                            <td >patient:{name}</td>
                            <td >doctor:{doctor}</td>
                            <td >date:{date}</td>
                        </tr>
                        <tr>
                            <td >Medicine List</td>
                        </tr>

                        {createMedicineList(prescription)}
   
                        </tbody>
                        </Table>
                        </div>
                    )
                 })            
            }
            else
            {
                return (
                    <div>
                    </div>
                    );
            }
        }

        const nameElement = createNameElement();

        const tableElement = createTableElement() 

        return (
            <div className="form-group">
                    
                <div>
                {tableElement}
                </div>
                
            </div>
            
        )
    }

}
