import React, { Component } from 'react';
import axios from 'axios';


export default class AddPrescription extends Component {
    constructor(props) {
        super(props);

        this.state = {
            contact: '',
            prescriptions : [],
            touched: {
                contact: false,
              }
            
        }
        this.onChangecontact = this.onChangecontact.bind(this);
        this.onClickSearch = this.onClickSearch.bind(this);
    }
    onChangecontact(e) {
        this.setState({
            contact: e.target.value
        });
    }

    onClickSearch(e) {
        e.preventDefault();
        var phone = this.state.contact;
        console.log(phone);
        axios.get('http://localhost:4000/getPrescription',{ params: {contact: phone}})
            .then(res => {
                console.log("here",res.data.medicine[0].doctor);
                this.setState({
                    prescriptions: res.data.medicine
                });
            });

    }



    render() {
        return (
            <div>
                <p>Welcome to AddPrescription  Component!!</p>
                <div>
                <div className="form-group">
                        <label>Contact: </label>
                        <input 
                                type="text" 
                                className={"form-control"}
                                value={this.state.contact}
                                onChange={this.onChangecontact}
                                />
                    </div>
                    <div className="form-group">
                        <input type="submit" onClick={this.onClickSearch} value="Create User" className="btn btn-primary" />
                    </div>
                    <div>
                        {this.state.prescriptions.map(prescription => (
                            <div>{prescription.medicine}</div>
                        ))}
                    </div>
                </div>
            </div>
            
        )
    }
}
