import React, { Component } from 'react';

export default class changePassword extends Component {
    constructor(props) {
        super(props);

        this.state = {
            old: '',
            new: '',
            renew:'',
        }
        this.onChangeold = this.onChangeold.bind(this);
        this.onChangenew = this.onChangenew.bind(this);
        this.onChangerenew = this.onChangerenew.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    

    onChangeold(e) {
        this.setState({
            old: e.target.value
        });
    }

    onChangenew(e) {
        this.setState({
            new: e.target.value
        });
    }
    onChangerenew(e) {
        this.setState({
            renew: e.target.value
        });
    }


    onSubmit(e) {
        e.preventDefault();
      
        let id= window.sessionStorage.getItem("id");

        console.log(`Form submitted:`);
        console.log(`id: ${id}`);
        console.log(`old: ${this.state.old}`);
        console.log(`new: ${this.state.new}`);
        console.log(`renew: ${this.state.renew}`);

        const user= {
            id:this.id,
            old:this.state.old,
            new:this.state.new,
            renew:this.state.renew
        }

        console.log(user)
        
        axios.post('http://localhost:4000/passwordreset', user)
            .then(res => {
                console.log(res.data);
                if(res.data==="success")
                {
                    alert("Password updated Successfuly");
                }
                else
                {
                    alert(res.data);
                }
            });

        this.setState({
            old: '',
            new: '',
            renew:''
        });
    }


    render() {
        return (
            <div>

            </div>
        )
    }
}
