import React, { Component } from 'react';
import axios from 'axios';
import "../css/style.css";

function validate(firstName,lastName,email, password) {
    // true means invalid, so our conditions got reversed
    return {
      firstName: firstName.length===0,
      lastName: lastName.length===0,
      email: email.length === 0,
      password: password.length === 0
    };
}

export default class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            password: '',
            userType: 'doctor',

            everFocusedFirstName: false,
            everFocusedLastName: false,
            everFocusedEmail: false,
            everFocusedPassword: false,
            inFocus: ""

        }
        this.onChangefirstName = this.onChangefirstName.bind(this);
        this.onChangelastName = this.onChangelastName.bind(this);
        this.onChangeemail = this.onChangeemail.bind(this);
        this.onChangepassword = this.onChangepassword.bind(this);
        this.onChangeuserType = this.onChangeuserType.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    
    onChangefirstName(e) {
        this.setState({
            firstName: e.target.value
        });
    }

    onChangelastName(e) {
        this.setState({
            lastName: e.target.value
        });
    }

    onChangeemail(e) {
        this.setState({
            email: e.target.value
        });
    }

    onChangepassword(e) {
        this.setState({
            password: e.target.value
        });
    }

    onChangeuserType(e) {
        this.setState({
            userType: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
        
        console.log(`Form submitted:`);
        console.log(`firstName: ${this.state.firstName}`);
        console.log(`lastName: ${this.state.lastName}`);
        console.log(`Email: ${this.state.email}`);
        console.log(`Password: ${this.state.password}`);
        console.log(`userType: ${this.state.userType}`);

        const user= {
            firstName:this.state.firstName,
            lastName:this.state.lastName,
            email:this.state.email,
            password:this.state.password,
            userType:this.state.userType
        }

        console.log(user)
        
        axios.post('http://localhost:4000/register', user)
            .then(res => {
                console.log(res.data);
                if(res.data==="success")
                {
                    alert("User Created Successfuly");
                }
                else
                {
                    alert(res.data);
                }
            });

        this.setState({
            firstName:'',
            lastName:'',
            email: '',
            password: '',
            userTpye:'doctor'
        });
    }


    canBeSubmitted() {
        const errors = validate(this.state.firstName,this.state.lastName,this.state.email, this.state.password);
        const isDisabled = Object.keys(errors).some(x => errors[x]);
        return !isDisabled;
      }

    render() {
        const errors = validate(this.state.firstName,this.state.lastName,this.state.email, this.state.password);
        const isDisabled = Object.keys(errors).some(x => errors[x]);
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New User</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>First Name: </label>
                        <input  type="text"
                                className={errors.firstName ? "form-control is-invalid" : "form-control"}
                                value={this.state.firstName}
                                onChange={this.onChangefirstName}
                                />
                    </div>
                    <div className="form-group">
                        <label>Last Name: </label>
                        <input 
                                type="text" 
                                className={errors.lastName ? "form-control is-invalid" : "form-control"}
                                value={this.state.lastName}
                                onChange={this.onChangelastName}
                                />
                    </div>
                    
                    <div className="form-group">
                    <label>Email: </label>
                    <input 
                            type="text" 
                            className={errors.email ? "form-control is-invalid" : "form-control"}
                            value={this.state.email}
                            onChange={this.onChangeemail}
                            />
                    </div>

                    <div className="form-group">
                        <label>Password: </label>
                        <input 
                                type="password" 
                                className={errors.password ? "form-control is-invalid" : "form-control"}
                                value={this.state.password}
                                onChange={this.onChangepassword}
                                />
                    </div>

                    <div>
                    <label >Choose a userType:</label>
                        <select value={this.state.userTpye} onChange={this.onChangeuserType}>
                        <option value="doctor">Doctor</option>
                        <option value="doctor assistant">Doctor Assistant</option>
                        <option value="lab staff">Lab Staff</option>
                        <option value="admin">Admin</option>
                        </select>
                        
                  </div>
                    <div className="form-group">
                        <input type="submit" disabled={isDisabled} value="Create User" className="btn btn-primary" />
                    </div>
                </form>

            </div>
        )
    }
}
