import React, { Component } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table'
import '../css/medicine.css';

export default class PatientList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            patientList:[],
            checkRequests:true,
            seen: false,     
       
        }

        this.handleClick = this.handleClick.bind(this);

    }



    handleClick(i, event) {
        
        alert(i)
    }

      render() {


        const createlistItems = () => {

            if(this.state.checkRequests)
            {
            
                const promise1 = new Promise(function(resolve, reject) {
                    
                    axios.post('http://localhost:4000/getpatientslist')
                    .then(res => {
                        resolve(res.data)
                    });
                });
                
                promise1.then((value) =>{
                    console.log(value);
                    if(value!="empty")
                    this.setState({
                        patientList: value,
                        checkRequests:false,
                    });
                });
            
            }

            if(this.state.patientList.length>0)
            {
                return this.state.patientList.map((item, index) => {
                    const { name, contact,gender} = item 
                    return (
                        <tr key={index} onClick={this.handleClick.bind(this, index)}>
                            <td>{index}</td>
                            <td>{name}</td>
                            <td>{contact}</td>
                            <td>{gender}</td>
                        </tr>
                        )
                 })            
            }
            else
            {
                return (
                    <div>
                    </div>
                    );
            }
        }        
      
        const listItems =createlistItems()

        return (
            <div className="form-group">
                   <div>
                    <h3>Patient List</h3>
                    <Table>
                    <thead>
                    <td>#</td>
                    <td>Name</td>
                    <td>Contact</td>
                    <td>Gender</td>
                    </thead>
                    <tbody>
                    {listItems}
                    </tbody>
                    
                    </Table>

                   </div>

                   <div  style={this.state.seen ? {display:'block'}:{display:'none'}}>
                   <div className="popup">
                   <div className="content">
                   <span className="close"  onClick={this.togglePop}>
                     &times;
                   </span>
                   <form onSubmit={this.onSubmitModal}>
                     <h3>Add Medicine Data!</h3>
                     <br />  
                     <input 
                       type="text" 
                       className="form-control"
                       placeholder="Drug Name"
                       required
                       value={this.state.name}
                       onChange={this.onChangename}
                       id="medicineName"
                       />
 
                     <br />
                     <input 
                     type="text" 
                     className="form-control"
                     placeholder="Form"
                     required
                     value={this.state.form}
                     onChange={this.onChangeform}
                     id="medicineForm"
                     />
 
                     <br />
                     <input 
                     type="text" 
                     className="form-control"
                     placeholder="Strength"
                     required
                     value={this.state.strength}
                     onChange={this.onChangestrength}
                     id="medicineStrength"
                     />
 
                     <br />
                     <input 
                     type="text" 
                     className="form-control"
                     placeholder="Active Ingredient"
                     required
                     value={this.state.ingredient}
                     onChange={this.onChangeingredient}
                     id="medicineIngredient"
                     />
 
                     <br />
                     <input type="submit" />
                   </form>
                    </div>
                 </div>

                </div>

            </div>
            
        )
    }

}
