import React, { Component } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table'

export default class DoctorFollowUps extends Component {
    constructor(props) {
        super(props);

        this.state = {
            requestList:[],
            checkRequests:true       
        }
        this.handleClick = this.handleClick.bind(this);

    }

    handleClick(i, event) {
        alert(i);
    }

      render() {


        const createlistItems = () => {

            if(this.state.checkRequests)
            {
            
                const user= {
                    email:localStorage.getItem("email"),
                }

                const promise1 = new Promise(function(resolve, reject) {
                    
                    axios.post('http://localhost:4000/getrequests', user)
                    .then(res => {
                        resolve(res.data)
                    });
                });
                
                promise1.then((value) =>{
                    console.log(value);
                    if(value!="empty")
                    this.setState({
                        requestList: value,
                        checkRequests:false,
                    });
                });
            
            }

            if(this.state.requestList.length>0)
            {
                return this.state.requestList.map((item, index) => {
                    const { name,contact, date} = item 
                    return (
                        <li key={index} onClick={this.handleClick.bind(this, index)}>
                            <h3>{index}</h3>
                        </li>
                        )
                 })            
            }
            else
            {
                return (
                    <div>
                    </div>
                    );
            }
        }        
      
        const listItems =createlistItems()

        return (
            <div className="form-group">
                   <div>
                    <h3>Follow Up Requests</h3>
                    <ul>
                    {listItems}
                    </ul>

                   </div>
            </div>
            
        )
    }

}
